

open Ez_subst.V1

let brace context s =
  match s with
  | "a" -> "AA" ^ context
  | "bb" -> "BB" ^ context
  | "b" -> "b"
  | x -> "<{" ^ x ^ "}>"
let paren context s =
  match s with
  | "c" -> "CC" ^ context
  | "dd" -> "DD" ^ context
  | "d" -> "d"
  | x -> "<(" ^ x ^ ")>"
let bracket context s =
  match s with
  | "e" -> "EE" ^ context
  | "ff" -> "FF" ^ context
  | "f" -> "f"
  | x -> "<[" ^ x ^ "]>"
let var context s =
  match s with
  | "g" -> "GG" ^ context
  | "hh" -> "HH" ^ context
  | "h" -> "h"
  | x -> "<[" ^ x ^ "]>"
let ctxt = "!"

let list = [
   "a", "AA" ;
   "bb", "BB" ;
   "b", "b" ;
   "c",  "CC" ;
   "dd", "DD" ;
   "d", "d" ;
   "e", "EE" ;
   "ff", "FF" ;
   "f", "f" ;
   "g", "GG" ;
   "hh", "HH" ;
   "h", "h" ;
 ]

let cases =
  List.map (fun fail ->
      List.map (fun sym ->
          List.map (fun brace ->
              List.map (fun paren ->
                  List.map (fun bracket ->
                      List.map (fun var ->
                          let ident =
                            Printf.sprintf
                              "fail=%b sym=%b brace=%b paren=%b bracket=%b var=%b"
                              fail
                              sym
                              (brace != None)
                              (paren != None)
                              (bracket != None)
                              (var != None)
                          in
                          let subst1 s =
                            EZ_SUBST.string ~sym ~fail
                              ?var ?brace ?paren ?bracket
                              ~ctxt s
                          in
                          let subst2 s =
                            EZ_SUBST.string_from_list ~sym ~fail
                              ~var:(var != None)
                              ~brace:(brace != None)
                              ~paren:(paren != None)
                              ~bracket:(bracket != None)
                              list s
                          in
                          [
                            ident ^ " EZ_SUBST.string", subst1 ;
                            ident ^ " EZ_SUBST.string_from_list", subst2 ;
                          ]
                        ) [ None ; Some var ]
                    ) [ None ; Some bracket ]
                ) [ None ; Some paren ]
            ) [ None ; Some brace ]
        ) [false;true]
    ) [false;true]
  |> List.flatten
  |> List.flatten
  |> List.flatten
  |> List.flatten
  |> List.flatten
  |> List.flatten

let sub tests  =
  List.iter (fun s->
      List.iter (fun (ident, subst) ->
          match subst s with
          | exception exn ->
              Printf.printf "%s: subst(%S) raised %s\n%!"
                ident s (Printexc.to_string exn)
          | res ->
              Printf.printf "%s: subst(%S)=%S\n%!"
                ident s res
        ) cases
    ) tests

